import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import data from './data/wellsut.geojson'
import {Source} from "react-map-gl";
import Layer from "react-mapbox-gl/lib-esm/layer"; // eslint-disable-line import/no-webpack-loader-syntax

mapboxgl.accessToken = 'pk.eyJ1Ijoic2xjbGVpIiwiYSI6ImNsMXV6czRnYjJkbnQzZG1qMHRxeGd0YmoifQ.mvtESpI1GCIdTrWSupNEIw';

export default function App() {
  const mapContainer = useRef(null);
  const [lng, setLong] = useState(-111.2);
  const [lat, setLat] = useState(40.5);
  const [zoom, setZoom] = useState(6);
  const [height,setHight]=useState(1500);

  const [hoveredDistrict, _setHoveredDistrict] = useState(null);
  const hoveredDistrictRef = useRef(hoveredDistrict);

    const setHoveredDistrict = data => {
        hoveredDistrictRef.current = data;
        _setHoveredDistrict(data);
    };

    useEffect(() => {
        let map = new mapboxgl.Map({
            container: mapContainer.current,
            style: 'mapbox://styles/mapbox/outdoors-v11',
            center: [lng, lat],
             zoom: zoom
        });

        map.once("load", function () {
        map.addSource('district-source', {
            'type': 'geojson',
            'data': data
        });

        map.addLayer({
            'id': 'district-layer',
            'type': 'fill',
            'source': 'district-source',
            'layout': {},
            'paint': {
                'fill-color': [
                    'match',
                    ['get', 'CD116FP'],
                    '01',
                    '#5AA5D7',
                    '02',
                    '#02735E',
                    '03',
                    '#00E0EF',
                    '04',
                    '#84D0D9',
                    '05',
                    '#202359',
                    '06',
                    '#CE7529',
                    '07',
                    '#00AE6C',
                    '08',
                    '#0056A3',
                    /* other */ '#ffffff'
                ],
                'fill-opacity': [
                    'case',
                    ['boolean', ['feature-state', 'hover'], false],
                    .8,
                    0.5
                ]
            }
        });

            map.on('mousemove', 'district-layer', function (e) {
                if (e.features.length > 0) {
                    if (hoveredDistrictRef.current && hoveredDistrictRef.current > -1) {

                        map.setFeatureState(
                            { source: 'district-source', id: hoveredDistrictRef.current },
                            { hover: false }
                        );
                    }

                    let _hoveredDistrict = e.features[0].id;

                    map.setFeatureState(
                        { source: 'district-source', id: _hoveredDistrict },
                        { hover: true }
                    );

                    setHoveredDistrict(_hoveredDistrict);
                }

            });

            // When the mouse leaves the state-fill layer, update the feature state of the
            // previously hovered feature.
            map.on('mouseleave', 'district-layer', function () {
                if (hoveredDistrictRef.current) {
                    map.setFeatureState(
                        { source: 'district-source', id: hoveredDistrictRef.current },
                        { hover: false }
                    );
                }
                setHoveredDistrict(null);
            });

            map.on('move', () => {
                const { lng, lat } = map.getCenter();

                setLong(lng.toFixed(4));
                setLat(lat.toFixed(4));
                setZoom(map.getZoom().toFixed(2));
            });

        });
        }, []);

  return (
      <div>
        <div className="sidebar">
          Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
        </div>
        <div ref={mapContainer} className="map-container" />
      </div>
  );
}
