              <td style={{ padding: "10px 5px" }}>
                <div id="measureAreaDiv">
                  <i
                    id="activeMeasureAreaIcon"
                    style={{ cursor: "pointer" }}
                    className="material-icons"
                    uib-tooltip="Measure Area"
                  >
                    area_chart
                  </i>
                  <i
                    id="deactivateMeasureAreaIcon"
                    style={{ cursor: "pointer", display: "none" }}
